svg.script:
var svgNS = "http://www.w3.org/2000/svg";
var numObjects = 10; // จำนวนข้อความ
var objects = [];
var box = {
  x: parseFloat(document.getElementById('box').getAttribute('x')),
  y: parseFloat(document.getElementById('box').getAttribute('y')),
  width: parseFloat(document.getElementById('box').getAttribute('width')),
  height: parseFloat(document.getElementById('box').getAttribute('height'))
};

for (var i = 0; i < numObjects; i++) {
  var text = document.createElementNS(svgNS, 'text');
  var fontSize = 16; // ขนาดตัวอักษร
  var textContent = 'xxxx'; // ข้อความที่จะแสดง
  text.setAttribute('font-size', fontSize);
  text.setAttribute('fill', 'red');

  // ประมาณความกว้างและความสูงของข้อความ
  var textWidth = textContent.length * fontSize * 0.6 * 2;
  var textHeight = fontSize;

  var x = box.x + Math.random() * (box.width - textWidth);
  var y = box.y + Math.random() * (box.height - textHeight) + textHeight;

  text.setAttribute('x', x);
  text.setAttribute('y', y);
  text.textContent = textContent;

  document.getElementById('objects').appendChild(text);

  var vx = (Math.random() - 0.5) * 4; // ความเร็วในแนว x
  var vy = (Math.random() - 0.5) * 4; // ความเร็วในแนว y

  objects.push({
    element: text,
    x: x,
    y: y,
    vx: vx,
    vy: vy,
    width: textWidth,
    height: textHeight
  });
}

function animate() {
  for (var i = 0; i < objects.length; i++) {
    var obj = objects[i];
    obj.x += obj.vx;
    obj.y += obj.vy;

    if (obj.x + obj.width >= box.x + box.width || obj.x <= box.x) {
      obj.vx = -obj.vx;
    }
    if (obj.y >= box.y + box.height || obj.y - obj.height <= box.y) {
      obj.vy = -obj.vy;
    }

    obj.element.setAttribute('x', obj.x);
    obj.element.setAttribute('y', obj.y);
  }
  requestAnimationFrame(animate);
}

animate();
